import React from 'react';
import classes from './MealItemForm.module.css';
import { Input } from '../../UI/Input';

export const MealsItemForm = (props) => {
  return (
    <form className={classes.form}>
      <Input
        label="Amount"
        input={{
          id: 'amt',
          type: 'number',
          min: 1,
          max: 15,
          step: 1,
          defaultValue: 1,
        }}
      />
      <button>Add</button>
    </form>
  );
};
